<?php
if ( ! defined( 'ABSPATH' ) ) exit;

if (!class_exists('Oc3Sengine_AdminController')) {

    class Oc3Sengine_AdminController extends Oc3Sengine_BaseController {

        const ADMIN_MENU = OC3SENGINE_PREFIX_LOW . 'settings'; //
        const ADMIN_MENU_CALLBACK = null; //can be view file or some callback function

        
        public $config_controller = false;
        public $search_controller = false;
        //public $admlogo = 'data:image/png;base64,';
        public $admlogo = ' data:image/svg+xml;base64,';
        public function __construct() {
            if (!class_exists('Oc3Sengine_AdminConfigController')) {
                $contr_path = OC3SENGINE_PATH . "/lib/controllers/AdminConfigController.php";
                include_once $contr_path;
            }
            $this->config_controller = new Oc3Sengine_AdminConfigController();
            
            if (!class_exists('Oc3Sengine_AdminSearchController')) {
                $contr_path = OC3SENGINE_PATH . "/lib/controllers/AdminSearchController.php";
                include_once $contr_path;
            }
            $this->search_controller = new Oc3Sengine_AdminSearchController();
            
            if (!class_exists('Oc3Sengine_UpdateUtils')) {
                $upd_path = OC3SENGINE_PATH . "/lib/helpers/UpdateUtils.php";
                include_once $upd_path;
            }
            
            add_action('admin_menu', array($this, 'registerAdminMenu'));

            add_action('add_meta_boxes', [$this, 'addChatMetaBox']);

            add_action('admin_enqueue_scripts', [$this, 'enqueueStyles']);


            add_action( 'plugins_loaded', ['Oc3Sengine_UpdateUtils','upgrade'] );
        }

        function addChatMetaBox() {
            if (!Oc3Sengine_Utils::checkEditAccess()) {
                return;
            }
            global $post;

            if (!empty($post)) {

                $selected_p_types = unserialize(get_option(OC3SENGINE_PREFIX_LOW . 'selected_types'));
                //var_dump($selected_p_types);
                if (!is_array($selected_p_types) || count($selected_p_types) == 0) {
                    //$selected_p_types = ['post', 'page'];
                    return;
                }

                add_meta_box(
                        'oc3sengine_gpt_box', // $id
                        'OC3 WPDEV Studio', // $title
                        [$this, 'showGptMeta'], // $callback
                        $selected_p_types, // $page
                        'normal', // $context
                        'high'); // $priority
            }
        }




        function enqueueStyles() {

            $screen = get_current_screen();
            //var_dump($screen->id);

            if (strpos($screen->id, 'oc3sengine_settings') !== false || strpos($screen->id, 'oc3sengine_search')  !== false   ) {
                wp_enqueue_style(
                        'jquery-ui',
                        OC3SENGINE_URL . '/views/resources/css/jquery-ui.css',
                        array(),'1.0'
                );
                wp_enqueue_script('jquery-ui-tabs');
            }

            wp_enqueue_style(
                    'oc3sengine',
                    OC3SENGINE_URL . '/views/resources/css/oc3sengine.css',
                    array(),'1.1'
            );
        }

        function showGptMeta() {

            
        }

        public function registerAdminMenu() {

        add_menu_page('Settings', 
        'OC3 Sengine',
                    'edit_posts', self::ADMIN_MENU, null,
                    $this->admlogo);
            $this->config_controller->registerAdminMenu();
            
            add_submenu_page(self::ADMIN_MENU, __('Search', 'oc3-sengine'), 
                __('Search', 'oc3-sengine'), 'edit_posts',
               OC3SENGINE_PREFIX_LOW . 'search', [$this->search_controller, 'showMainView']);
            
            if (!Oc3Sengine_Utils::checkEditInstructionAccess()) {
                return;
            }
            
            
        }

        function showSettings() {
            
        }


    }

}
